mapper.select:
SELECT
	a.id,
	a.model_name modelName
FROM
	code_model a
WHERE a.group_id = #{groupId}
	AND a.model_name = #{modelName}
	AND a.model_type = #{modelType}

SELECT
	a.id,
	a.model_name modelName
FROM
	code_model a
WHERE a.id != #{id}
	AND a.group_id = #{groupId}
	AND a.model_name = #{modelName}
	AND a.model_type = #{modelType}
